flags: O_RDONLY, O_WRONLY, O_RDWR, O_CREAT, &hellip;
 #include &lt;fcntl.h&gt;#include &lt;stdio.h&gt; int main(void) { int fd; fd = open(&#34;/opt/c/hello.txt&#34;, O_RDONLY); if (fd == -1) perror(&#34;Error&#34;); else printf(&#34;%d&#34;, fd); }  int create(const char *name, mode_t mode);
